mapper.sql:
select c.id, c.code, c.name, c.simple_name, c.wo_id, owners.owner_name, c.country, c.province, c.city, c.address, c.zip_code, c.remark, c.create_time, user1.real_name as create_user, user2.real_name as update_user, c.update_time, IF(c.status = 1, '启用', '未启用') as status from basics_customers c LEFT JOIN blade_user user1 ON user1.id = c.create_user LEFT JOIN blade_user user2 ON user2.id = c.update_user LEFT JOIN wms_owner owners on owners.wo_id=c.wo_id
AND c.code LIKE concat('%',#{query.code},'%')
AND c.name LIKE concat('%',#{query.name},'%')
AND c.simple_name LIKE concat('%',#{query.simpleName},'%')
AND c.create_time >= #{query.createTimeBegin}
AND c.create_time <= #{query.createTimeEnd}
AND c.update_time >= #{query.updateTimeBegin}
AND c.update_time <= #{query.updateTimeEnd}
AND c.is_deleted = '0'
