mapper.sql:
SELECT l.loc_id, l.loc_code, l.agv_location, l.agv_level, l.agv_point, w.wh_name as whName, z.zone_name as zoneName, d1.dict_value as locType, d2.dict_value as locCategory, d3.dict_value as locHandling, d4.dict_value as abc, d5.dict_value as locFlag, l.logic_allocation, l.loc_level, l.loc_column, l.loc_bank, l.put_order, lt.code as lpn_type_code, IF(l.status = 1, '是', '否') as status FROM wms_location l LEFT JOIN wms_warehouse w ON w.wh_id = l.wh_id LEFT JOIN wms_zone z ON z.zone_id = l.zone_id LEFT JOIN wms_lpn_type lt ON lt.id = l.lpn_type_id LEFT JOIN blade_dict d1 ON l.loc_type = d1.dict_key and d1.code = 'loc_type' LEFT JOIN blade_dict d2 ON l.loc_category = d2.dict_key and d2.code = 'loc_category' LEFT JOIN blade_dict d3 ON l.loc_handling = d3.dict_key and d3.code = 'loc_handling' LEFT JOIN blade_dict d4 ON l.abc = d4.dict_key and d4.code = 'abc' LEFT JOIN blade_dict d5 ON l.loc_flag = d5.dict_key and d5.code = 'loc_flag'
l.is_deleted = 0
AND l.loc_code LIKE CONCAT('%', #{param.locCode}, '%')
AND l.loc_level LIKE CONCAT('%', #{param.locLevel}, '%')
AND l.loc_bank LIKE CONCAT('%', #{param.locBank}, '%')
AND l.loc_column LIKE CONCAT('%', #{param.locColumn}, '%')
AND l.agv_location LIKE CONCAT('%', #{param.agvLocation}, '%')
AND l.agv_level LIKE CONCAT('%', #{param.agvLevel}, '%')
AND l.agv_point LIKE CONCAT('%', #{param.agvPoint}, '%')
