mapper.select:
select s.wh_id, w.wh_name, s.loc_id, s.loc_code, s.box_code, s.sku_id, s.sku_code, s.sku_name, cd.count_bill_no, l.last_loc_count_date, ch.count_bill_state, s.sku_lot2 from wms_stock s LEFT JOIN wms_warehouse w on s.wh_id = w.wh_id LEFT JOIN wms_count_detail cd on s.box_code = cd.box_code LEFT JOIN wms_location l on s.loc_id = l.loc_id left join wms_count_header ch on cd.count_bill_no = ch.count_bill_no
AND s.sku_lot2 = #{param.skuLot2}
AND s.zone_id in
#{item}
GROUP BY s.loc_code, s.box_code, s.sku_code ORDER BY s.box_code desc
