SELECT h.asn_bill_id, h.asn_bill_no, h.asn_bill_state, b.bill_type_name as billTypeName, h.supplier_code, h.supplier_name, h.external_order_no, h.external_create_user, w.wh_name as whName, h.asn_bill_remark, h.create_time, u.real_name as createUser, h.update_time FROM asn_header h LEFT JOIN blade_user u ON h.create_user = u.id LEFT JOIN wms_bill_type b ON h.bill_type_cd = b.bill_type_cd LEFT JOIN wms_warehouse w ON h.wh_id = w.wh_id
h.is_deleted = 0
AND h.asn_bill_no = #{params.asnBillNo}
AND d.sku_code IN
#{item}
AND h.asn_bill_state IN
#{item}
AND h.create_time >= #{params.createTimeBegin}
AND h.create_time <= #{params.createTimeEnd}
AND (h.supplier_code LIKE concat('%',#{params.supplier},'%') OR h.supplier_name LIKE concat('%',#{params.supplier},'%'))
AND h.external_order_no = #{params.externalOrderNo}
AND h.external_create_user = #{params.externalCreateUser}
AND h.wh_id IN
#{item}
ORDER BY create_time DESC

