select ws.serial_number, s.sku_code, s.sku_name, s.stock_status, s.sku_lot1, s.wsu_code, s.loc_code, s.zone_code, s.box_code, s.lpn_code, s.sku_lot2, s.sku_lot3, s.sku_lot4, s.sku_lot5, s.sku_lot6, s.sku_lot7, s.sku_lot8, s.wh_code, wo.owner_code, wo.owner_name, s.last_in_time, s.last_out_time from wms_serial ws left join wms_stock s on ws.stock_id = s.stock_id left join wms_owner wo on s.wo_id = wo.wo_id left join wms_zone wz on s.zone_id = wz.zone_id
AND ws.serial_number LIKE concat('%',#{query.serial},'%')
AND s.sku_id in
#{item}
AND s.loc_id in
#{item}
AND s.sku_lot1 LIKE concat('%',#{query.skuLot1},'%')
AND s.box_code LIKE concat('%',#{query.boxCode},'%')
AND ws.serial_number >= #{query.serialBegin}
AND ws.serial_number <= #{query.serialEnd}
and s.stay_stock_qty + s.stock_qty > s.pick_qty and ws.is_deleted = 0

select stock_id,stock_qty,wh_id,loc_code,box_code,sum(stock_qty-pick_qty)
total_qty,sku_id,wsp_id,sku_level,sku_code,sku_name,wsu_code,sku_lot1,sku_lot2,sku_lot3 from wms_stock


        AND loc_code = #{locCode}


        AND box_code = #{boxCode}


        AND sku_code = #{skuCode}

    AND is_deleted = 0
    AND status = 1

GROUP BY loc_code,box_code,sku_code
